INSERT INTO YOGA_POSE (id, sanskrit_name, english_name, description, difficulty_level, image_url) VALUES
    (1, 'Tadasana', 'Mountain Pose', 'This is the foundational pose for all standing postures. It teaches you to stand tall, grounded, and aware. It engages the legs, improves posture, and centers the mind.', 'Easy Beginner', 'https://pocketyoga.com/assets/images/full/MountainArmsSide.png');
INSERT INTO YOGA_POSE (id, sanskrit_name, english_name, description, difficulty_level, image_url) VALUES
    (2, 'Adho Mukha Svanasana', 'Downward-Facing Dog', 'A cornerstone inversion that deeply stretches the hamstrings, calves, and spine, while strengthening the arms and shoulders. It is a full-body stretch that also helps to energize and rejuvenate.', 'Beginner', 'https://pocketyoga.com/assets/images/full/DownwardDog.png');
INSERT INTO YOGA_POSE (id, sanskrit_name, english_name, description, difficulty_level, image_url) VALUES
    (3, 'Virabhadrasana II', 'Warrior II', 'A powerful standing pose that builds strength and stamina in the legs, opens the hips and chest, and develops concentration and stability. It embodies the focus and strength of a warrior.', 'Intermediate', 'https://pocketyoga.com/assets/images/full/WarriorII_L.png');
INSERT INTO YOGA_POSE (id, sanskrit_name, english_name, description, difficulty_level, image_url) VALUES
    (4, 'Trikonasana', 'Triangle Pose', 'A deep side-body stretch that opens the hips, hamstrings, and spine. It also strengthens the thighs and core while stretching the muscles around the chest and shoulders.', 'Intermediate', 'https://pocketyoga.com/assets/images/full/TriangleForward_L.png');
INSERT INTO YOGA_POSE (id, sanskrit_name, english_name, description, difficulty_level, image_url) VALUES
    (5, 'Ardha Chandrasana', 'Half Moon Pose', 'A challenging balancing pose that strengthens the thighs, ankles, and core. It opens the hips and chest, improves coordination, and helps to relieve stress and anxiety.', 'Intermediate', 'https://pocketyoga.com/assets/images/full/CrescentMoon_R.png');
INSERT INTO YOGA_POSE (id, sanskrit_name, english_name, description, difficulty_level, image_url) VALUES
    (6, 'Bakasana', 'Crow Pose', 'An foundational arm balance that builds significant strength in the arms, wrists, and core. It teaches balance and focus, and helps to overcome fear.', 'Advanced', 'https://pocketyoga.com/assets/images/full/Crane.png');
INSERT INTO YOGA_POSE (id, sanskrit_name, english_name, description, difficulty_level, image_url) VALUES
    (7, 'Sirsasana', 'Supported Headstand', 'Often called the "king of all poses," this is a full inversion that strengthens the entire body, especially the core and upper body. It calms the brain, improves circulation, and builds confidence.', 'Advanced', 'https://pocketyoga.com/assets/images/full/HeadstandSupported.png');
INSERT INTO YOGA_POSE (id, sanskrit_name, english_name, description, difficulty_level, image_url) VALUES
    (8, 'Balasana', 'Child''s Pose', 'A restful and restorative pose that gently stretches the hips, thighs, and ankles. It is used to calm the mind, relieve stress, and ease back pain. It is a grounding pose to return to at any time.', 'Beginner', 'https://pocketyoga.com/assets/images/full/ChildTraditional.png');
INSERT INTO YOGA_POSE (id, sanskrit_name, english_name, description, difficulty_level, image_url) VALUES
    (9, 'Marjaryasana/Bitilasana', 'Cat-Cow Stretch', 'A dynamic flow between two poses. Cat (Marjaryasana) rounds the back to stretch the spine, while Cow (Bitilasana) drops the belly to open the chest. It is excellent for warming up the spine and relieving tension.', 'Beginner', 'https://pocketyoga.com/assets/images/full/Cat.png');
INSERT INTO YOGA_POSE (id, sanskrit_name, english_name, description, difficulty_level, image_url) VALUES
    (10, 'Bhujangasana', 'Cobra Pose', 'A gentle backbend that opens the chest and shoulders. It strengthens the spine, stretches the abdominal muscles, and helps to relieve stress. It is a key part of the Sun Salutation.', 'Beginner', 'https://pocketyoga.com/assets/images/full/CobraFull.png');
INSERT INTO YOGA_POSE (id, sanskrit_name, english_name, description, difficulty_level, image_url) VALUES
    (11, 'Eka Pada Rajakapotasana', 'Pigeon Pose', 'A powerful hip-opening pose that stretches the outer hip, groin, and psoas muscles. It is excellent for releasing tension and increasing flexibility in the hips.', 'Intermediate', 'https://pocketyoga.com/assets/images/full/PigeonKingFull.png');
INSERT INTO YOGA_POSE (id, sanskrit_name, english_name, description, difficulty_level, image_url) VALUES
    (12, 'Ustrasana', 'Camel Pose', 'A deep backbend that opens the entire front of the body, including the chest, abdomen, and hip flexors. It improves spinal flexibility and stimulates the organs.', 'Intermediate', 'https://pocketyoga.com/assets/images/full/Camel.png');
INSERT INTO YOGA_POSE (id, sanskrit_name, english_name, description, difficulty_level, image_url) VALUES
    (13, 'Navasana', 'Boat Pose', 'An intense core-strengthening pose that builds abdominal and hip flexor strength. It also challenges your balance and helps to improve focus and digestion.', 'Intermediate', 'https://pocketyoga.com/assets/images/full/BoatFull.png');
INSERT INTO YOGA_POSE (id, sanskrit_name, english_name, description, difficulty_level, image_url) VALUES
    (14, 'Garudasana', 'Eagle Pose', 'A complex balancing pose that involves "binding" the arms and legs. It improves balance and concentration, while stretching the shoulders, upper back, and outer hips.', 'Intermediate', 'https://pocketyoga.com/assets/images/full/Eagle_R.png');
INSERT INTO YOGA_POSE (id, sanskrit_name, english_name, description, difficulty_level, image_url) VALUES
    (15, 'Astavakrasana', 'Eight-Angle Pose', 'An advanced and complex arm balance that requires significant core strength, arm strength, and hip flexibility. It is a puzzle for the body that teaches leverage and balance.', 'Advanced', 'https://pocketyoga.com/assets/images/full/EightAngle_R.png');
INSERT INTO YOGA_POSE (id, sanskrit_name, english_name, description, difficulty_level, image_url) VALUES
    (16, 'Natarajasana', 'King Dancer Pose', 'A beautiful and challenging balancing pose that is also a deep backbend. It opens the shoulders and chest, strengthens the legs, and improves balance and poise.', 'Advanced', 'httpsS://pocketyoga.com/assets/images/full/LordOfTheDanceFull_L.png');
